CREATE TABLE `Usuario` (
  `idUsuario` int NOT NULL AUTO_INCREMENT,
  `nome` varchar(500) NOT NULL,
  `email` varchar(100) NOT NULL,
  `senha` varchar(8) NOT NULL,
  `is_admin` tinyint NOT NULL DEFAULT '0',
  `curso` varchar(100) NOT NULL,
  `matricula` int NOT NULL,
  PRIMARY KEY (`idUsuario`),
  UNIQUE KEY `idUsuario_UNIQUE` (`idUsuario`),
  UNIQUE KEY `matrícula_UNIQUE` (`matricula`)
) ;


CREATE TABLE `Departamento` (
  `idDepartamento` int NOT NULL,
  `nome` varchar(1000) NOT NULL,
  PRIMARY KEY (`idDepartamento`)
);

CREATE TABLE `Disciplina` (
  `idDisciplina` varchar(20) NOT NULL,
  `nome` varchar(1000) NOT NULL,
  PRIMARY KEY (`idDisciplina`),
  UNIQUE KEY `idDisciplina_UNIQUE` (`idDisciplina`)
);

CREATE TABLE `Professor` (
  `idProfessor` int NOT NULL AUTO_INCREMENT,
  `nome` varchar(500) NOT NULL,
  `idDepartamento` int NOT NULL,
  PRIMARY KEY (`idProfessor`),
  UNIQUE KEY `idProfessor_UNIQUE` (`idProfessor`),
  UNIQUE KEY `nome_UNIQUE` (`nome`),
  KEY `fk_id_departamento_idx` (`idDepartamento`),
  CONSTRAINT `fk_id_departamento` FOREIGN KEY (`idDepartamento`) REFERENCES `Departamento` (`idDepartamento`) ON DELETE CASCADE
);

CREATE TABLE `Turma` (
  `idTurma` int NOT NULL AUTO_INCREMENT,
  `numeroTurma` varchar(3) NOT NULL,
  `periodo` varchar(20) NOT NULL,
  `codDisciplinaTurma` varchar(20) NOT NULL,
  `codProfessorTurma` int NOT NULL,
  PRIMARY KEY (`idTurma`),
  UNIQUE KEY `idTurma_UNIQUE` (`idTurma`),
  KEY `fk_TurmaProfessor_2_idx` (`codProfessorTurma`),
  KEY `fk_TurmaDisciplina_1_idx` (`codDisciplinaTurma`),
  CONSTRAINT `fk_TurmaDisciplina_1` FOREIGN KEY (`codDisciplinaTurma`) REFERENCES `Disciplina` (`idDisciplina`) ON DELETE CASCADE,
  CONSTRAINT `fk_TurmaProfessor_2` FOREIGN KEY (`codProfessorTurma`) REFERENCES `Professor` (`idProfessor`) ON DELETE CASCADE
);


CREATE TABLE `AvaliacaoProfessor` (
  `idAvaliacaoProfessor` int NOT NULL AUTO_INCREMENT,
  `comentario` varchar(120) DEFAULT NULL,
  `classificacao` int NOT NULL,
  `codProfessorAvaliado` int NOT NULL,
  `codUsuarioAvaliacao` int NOT NULL,
  PRIMARY KEY (`idAvaliacaoProfessor`),
  UNIQUE KEY `idAvaliacaoProfessor_UNIQUE` (`idAvaliacaoProfessor`),
  KEY `fk_id_professor_idx` (`codProfessorAvaliado`),
  KEY `fk_id_usuario_idx` (`codUsuarioAvaliacao`),
  CONSTRAINT `fk_AvaliacaoProfessor_1` FOREIGN KEY (`codProfessorAvaliado`) REFERENCES `Professor` (`idProfessor`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `fk_AvaliacaoProfessor_2` FOREIGN KEY (`codUsuarioAvaliacao`) REFERENCES `Usuario` (`idUsuario`) ON DELETE CASCADE
);


CREATE TABLE `AvaliacaoTurma` (
  `idAvaliacaoTurma` int NOT NULL AUTO_INCREMENT,
  `comentario` varchar(120) DEFAULT NULL,
  `classificacao` int NOT NULL,
  `codTurmaAvaliada` int NOT NULL,
  `codUsuarioAvaliacao` int NOT NULL,
  PRIMARY KEY (`idAvaliacaoTurma`),
  UNIQUE KEY `idAvaliacaoTurma_UNIQUE` (`idAvaliacaoTurma`),
  KEY `fk_TurmaAvaliacaoTurma_1_idx` (`codTurmaAvaliada`),
  KEY `fk_UsuarioAvaliacaoTurma_2_idx` (`codUsuarioAvaliacao`),
  CONSTRAINT `fk_TurmaAvaliacaoTurma_1` FOREIGN KEY (`codTurmaAvaliada`) REFERENCES `Turma` (`idTurma`) ON DELETE CASCADE,
  CONSTRAINT `fk_UsuarioAvaliacaoTurma_2` FOREIGN KEY (`codUsuarioAvaliacao`) REFERENCES `Usuario` (`idUsuario`) ON DELETE CASCADE
);


CREATE TABLE `DenunciaProfessor` (
  `idDenunciaProfessor` int NOT NULL AUTO_INCREMENT,
  `comentario` varchar(120) NOT NULL,
  `status` tinyint NOT NULL,
  `codAvaliacaoProfessorDenuncia` int NOT NULL,
  `codUsuarioDenunciaProfessor` int NOT NULL,
  PRIMARY KEY (`idDenunciaProfessor`),
  UNIQUE KEY `idDenunciaProfessor_UNIQUE` (`idDenunciaProfessor`),
  KEY `fk_AvaliacaoDenunciaProfessor_1_idx` (`codAvaliacaoProfessorDenuncia`),
  KEY `fk_DenunciaProfessor_1_idx` (`codUsuarioDenunciaProfessor`),
  CONSTRAINT `fk_AvaliacaoDenunciaProfessor_1` FOREIGN KEY (`codAvaliacaoProfessorDenuncia`) REFERENCES `AvaliacaoProfessor` (`idAvaliacaoProfessor`) ON DELETE CASCADE ON UPDATE RESTRICT,
  CONSTRAINT `fk_DenunciaProfessor_2` FOREIGN KEY (`codUsuarioDenunciaProfessor`) REFERENCES `Usuario` (`idUsuario`)
);


CREATE TABLE `DenunciaTurma` (
  `idDenunciaTurma` int NOT NULL AUTO_INCREMENT,
  `comentario` varchar(120) NOT NULL,
  `status` tinyint NOT NULL,
  `codDenunciaTurmaAvaliacao` int NOT NULL,
  `codUsuarioDenunciaTurma` int NOT NULL,
  PRIMARY KEY (`idDenunciaTurma`),
  UNIQUE KEY `idDenunciaTurma_UNIQUE` (`idDenunciaTurma`),
  KEY `fk_DenunciaTurmaAvaliacao_1_idx` (`codDenunciaTurmaAvaliacao`),
  KEY `fk_DenunciaTurma_2_idx` (`codUsuarioDenunciaTurma`),
  CONSTRAINT `fk_DenunciaTurma_1` FOREIGN KEY (`codDenunciaTurmaAvaliacao`) REFERENCES `AvaliacaoTurma` (`idAvaliacaoTurma`) ON DELETE CASCADE,
  CONSTRAINT `fk_DenunciaTurma_2` FOREIGN KEY (`codUsuarioDenunciaTurma`) REFERENCES `Usuario` (`idUsuario`) ON DELETE CASCADE
);

CREATE VIEW `turmaDisciplina` AS select `t`.`idTurma` AS `idTurma`,`t`.`periodo` AS `periodo`,`t`.`numeroTurma` AS `numeroTurma`,`dis`.`nome` AS `nome` from (`avaliacaoUnB`.`Turma` `t` left join `avaliacaoUnB`.`Disciplina` `dis` on((`t`.`codDisciplinaTurma` = `dis`.`idDisciplina`)));